// JavaScript kh√¥ng thay ƒë·ªïi v√¨ logic v·∫´n ho·∫°t ƒë·ªông t·ªët
document.addEventListener('DOMContentLoaded', () => {
    const optionsDataSource = document.getElementById('options-data');
    const container = document.getElementById('button-container');

    if (!optionsDataSource || !container) {
        console.error("Kh√¥ng t√¨m th·∫•y container ho·∫∑c ngu·ªìn d·ªØ li·ªáu cho c√°c t√πy ch·ªçn.");
        return;
    }

    const optionsText = optionsDataSource.innerHTML.trim();
    const lines = optionsText.split('\n');

    container.innerHTML = ''; 

    lines.forEach(line => {
        const trimmedLine = line.trim();
        if (!trimmedLine) return;

        const match = trimmedLine.match(/^(?:\s*\d+\.?\s*)?(.*)$/);
        const actionText = match ? match[1].trim() : trimmedLine;

        const button = document.createElement('button');
        button.className = 'option-button';
        button.textContent = trimmedLine; 
        button.setAttribute('data-action', actionText); 

        button.addEventListener('click', (event) => {
            event.stopPropagation();
            const textToSend = button.getAttribute('data-action');
            if (!textToSend) return;

            try {
                const input = window.parent.document.querySelector('#send_textarea');
                if (input) {
                    const currentValue = input.value.trim();
                    input.value = currentValue ? `${currentValue}\n${textToSend}` : textToSend;
                    input.dispatchEvent(new Event('input', { bubbles: true }));
                }
            } catch (error) { 
                console.error('L·ªói khi t∆∞∆°ng t√°c v·ªõi SillyTavern:', error); 
            }

            document.querySelectorAll('#button-container .option-button').forEach(b => b.classList.remove('active'));
            button.classList.add('active');
        });

        container.appendChild(button);
    });
});
</script>

